mapper.select:
select
  CityID, CityName, ZipCode, ProvinceID, DateCreated, DateUpdated
from car_timetable.car_city


        and CityID = #{cityid}


        and CityName = #{cityname}


        and ZipCode = #{zipcode}


        and ProvinceID = #{provinceid}


        and DateCreated = #{datecreated}


        and DateUpdated = #{dateupdated}
